definition.script:
pipeline {
    agent{
        label{
            label ''
        }
    }
	stages {
	    stage('Clean workspace'){
	        steps{
	            println 'Se limpia el workspace'
                cleanWs()
	        }
	    }
	    stage('Donwload Source') {
        	steps {
        	    script{
        	        sh '/usr/bin/git clone ssh://git@steps.everis.com:766/INNOVASEV/ScrumMeter.git -b Development'
        	        sh 'chown -R jenkins:jenkins $WORKSPACE/ScrumMeter/'
        	    }
        	}
    	}
    	stage('Install package and build'){
    	    steps{
    	        script{
    	            sh 'dotnet build $WORKSPACE/ScrumMeter/everisapi.API/'
    	        }
    	    }
    	}
    	stage('Build test'){
    	    steps{
    	        script{

    	            sh 'dotnet add $WORKSPACE/ScrumMeter/everisapiTest/everisapiTest.csproj package  dotnet-reportgenerator-cli --version 4.2.2'
    	            sh 'dotnet build $WORKSPACE/ScrumMeter/everisapiTest/'
    	        }
    	    }
    	}
    	stage('Execute test'){
    	    steps{
    	        script{

    	            sh 'dotnet test $WORKSPACE/ScrumMeter/everisapiTest/everisapiTest.csproj /p:CollectCoverage=true /p:CoverletOutputFormat=opencover /p:CoverletOutput="$WORKSPACE/ScrumMeter/everisapiTest/TestResult/" /p:Exclude="[xunit*]*"'
    	            sh 'cd $WORKSPACE/ScrumMeter/everisapiTest/ && dotnet reportgenerator everisapiTest.csproj "-reports:./TestResult/coverage.opencover.xml" -targetdir:"./TestResults/Reports" -reporttypes:HTML'
    	        }
    	    }
    	}
    	stage('Sonarqube') {
            environment {
            scannerHome = tool 'SonarQubeScanner'
            }
            steps {
                withSonarQubeEnv('sonarqube') {
                sh "${scannerHome}/bin/sonar-scanner"
                }
                timeout(time: 10, unit: 'MINUTES') {
                waitForQualityGate abortPipeline: true
            }
        }
    }
}
	}
}
